import React from "react";
import loginImg from "../../BK.jpg";
import { Formik } from "formik";
import * as EmailValidator from "email-validator"; 


export class Login extends React.Component {
    
    constructor(props) {
      super(props);
    }
    
    render() {
      return (
        <Formik
          initialValues={{ email: "", password: "" }}
          onSubmit={(values, { setSubmitting }) => {
            setTimeout(() => {
              console.log("Logging in", values);
              setSubmitting(false);
            }, 500);
            
          }}
          validate={values => {
              let errors = {};
              if (!values.email) {
                errors.email = "Required";
              } else if (!EmailValidator.validate(values.email)) {
                errors.email = "Invalid email address.";
              }
              const passwordRegex = /(?=.*[0-9])/;
              if (!values.password) {
                errors.password = "Required";
              } else if (values.password.length < 8) {
                  errors.password = "Password must be 8 characters long.";
                } else if (!passwordRegex.test(values.password)) {
                  errors.password = "Invalid password. Must contain one number.";
                }
                return errors;
            }}
      
        >
      
        {props => {
          const {
            values,
            touched,
            errors,
            isSubmitting,
            handleChange,
            handleBlur,
            handleSubmit
          } = props;
          return (
            <div className="base-container" ref={this.props.containerRef}>
              <div className="header">Login</div>
              <div className="content">
                <div className="image">
                  <img src={loginImg} />
                </div>
                <div className="form">
                  <div className="form-group">
                    <label htmlFor="email">email</label>
                    <input id = "email"type="text" name="email" placeholder="email" value={values.email} onChange={handleChange} onBlur={handleBlur} className={errors.email && touched.email && "error"} />
                    {errors.email && touched.email && (
                        <div className="input-feedback">{errors.email}</div>
                        )}
                  </div>
                  <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input id = "password" type="password" name="password" placeholder="password" value={values.password} onChange={handleChange} onBlur={handleBlur} className={errors.password && touched.password && "error"}/>
                    {errors.password && touched.password && (
                    <div className="input-feedback">{errors.password}</div>
                    )}
                  </div>
                </div>
              </div>
              <div className="footer">
                <button type="button" className="btn">
                  Login
                </button>
              </div>
            </div>
          );
        }}
        
      </Formik>
          );
        }
  }
