import React from "react";
import loginImg from "../../BK.jpg";
import { Formik } from "formik";
import * as EmailValidator from "email-validator"; // used when validating with a self-implemented approach
import * as Yup from "yup";
export class Login extends React.Component{
    constructor(props){
        super(props);
    }
    render(){
        <Formik
        >
        {props => {
            const {
            values,
            touched,
            errors,
            isSubmitting,
            handleChange,
            handleBlur,
            handleSubmit
        } = props;
        return (
        
        <div className="base-container" ref={this.props.containerRef}>
                <div className="header">Login</div>
                <div class className="content">
                    <div className="image">
                        <img src={loginImg} />
                    </div>
                    <div className="form">
                        <div className="form-group">
                            <label htmlFor="username">Username</label>
                            <input id="username" type="text" name="username" placeholder="username"></input>
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">Password</label>
                            <input id="password" type="password" name="password" placeholder="password"></input>
                        </div>
                        
                    </div>
                </div>
                <div className="footer">
                    <button type="button" className="btn">
                        Login
                    </button>
                </div>
                    
            </div>

        );
        }}
        validate={values => {
            let errors = {};
            if (!values.username) {
                errors.username = "Required";
            } else if (!EmailValidator.validate(values.username)) {
                errors.username = "Invalid email address.";
            }
            const passwordRegex = /(?=.*[0-9])/;
            if (!values.password) {
                errors.password = "Required";
            } else if (values.password.length < 8) {
                errors.password = "Password must be 8 characters long.";
            } else if (!passwordRegex.test(values.password)) {
                errors.password = "Invalid password. Must contain one number.";
            }
            return errors;
            }
        }
        </Formik>
    }
}
